def solve():
    with open('/home/chal/enc_flag', 'r') as f:
        data = f.read().strip()

    # Try to decode repeatedly
    count = 0
    while True:
        try:
            # We need to handle potential newlines or whitespace in the encoded data
            # Although f.read().strip() handles the initial one, intermediate decodes might produce bytes that need to be treated as string for the next decode.
            # Base64 decode usually takes bytes-like or ascii string.

            # If data is bytes, decode to string to re-feed into b64decode if it's base64 encoded text
            if isinstance(data, bytes):
                try:
                    data = data.decode('utf-8').strip()
                except UnicodeDecodeError:
                    # If we can't decode to utf-8, we probably reached the binary flag or garbage.
                    # Stop and print the last bytes
                    print(f"Stopping at iteration {count} (UnicodeDecodeError)")
                    print(data)
                    break

            decoded = base64.b64decode(data)

            # Check if empty
            if not decoded:
                break

            data = decoded
            count += 1

        except Exception as e:
            # If decoding fails, we likely found the plaintext (or it's not base64 anymore)
            print(f"Stopping at iteration {count} due to: {e}")
            print("Last data found:")
            print(data)
            break

    print("-" * 20)
    print("Final Result:")
    try:
        if isinstance(data, bytes):
            print(data.decode('utf-8'))
        else:
            print(data)
    except:
        print(data)

if __name__ == "__main__":
    solve()
